
 class Player {
private String name;

private int position;
public Player(String name,int position) {
	// TODO Auto-generated constructor stub
this.name=name;
this.position=position;
}
String getName()
{
	return name;
}
void setName(String name)
{
this.name=name;	
}
int getPosition()
{
	return position;
}

void setPosition(int num)
{
	position=num;
}
}
